import { READ_ALL_DATA, NO_DATA } from "../types";

export const initialState = {
    db:[]
}

export function crudReducer(state, action){
    switch (action.type) {
        case READ_ALL_DATA: {
            console.log(action.payload);
            return {
                ...state,
                db: action.payload.map(data => data)
            }
        }
        case NO_DATA:
            return initialState;
        default:
            return state;
    }
}
